INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `heading`, `body`, `course`, `unit`, `yearFinishing`, `userProfile`, `evaluation`, `grounding`, `mockups`, `prototypes`, `sketching`, `presentation`, `projectDescription`, `schedule`, `designBrief`, `dueDate`, `submissionDetails`, `submissionResources`, `interactionDesignAssignmentNumber`, `researchArea`, `publicationAbstract`, `researchAbout`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('54', '67', 'en_us', NULL, NULL, '', 'IDEA', NULL, NULL, '<p>Oliver Bown is a British researcher and electronic music practitioner. He joined the Design Lab, at the Faculty of Architecture, Design and Planning, in June 2011. His research interests include digital music, music software and performance systems, computational creativity, biologically-inspired computing, complex systems and ecosystems, multi-agent modelling, models and theories of cultural dynamics, and human evolution, particularly with respect to human artistic behaviour.</p>', '', '', '', '', '', '', NULL, NULL, '', NULL, '', '', NULL, NULL, '', NULL, '2013-10-23 02:58:33', '2013-10-23 02:59:24', 'c46ebb58-a083-4af1-aa6f-5805220fdd1b');
